// Init WP Image Browser
SUPER.init_image_browser = function(){
    var $ = jQuery;

    $('.browse-csv-import-file').each(function () {
        var $this = $(this);
        var $title = 'Select a CSV file';
        var $btn_name = 'Add CSV';
        var $button = $this.children('.button');
        var $preview = $this.children('.file-preview');
        var $field = $this.children('input');
        var $frame;
        var $id = $field.val();
        $preview.on('click', 'a.delete', function () {
            $field.val('');
            $preview.html('');
        });
        $button.on('click', function () {
            $('.ui-widget-overlay').hide();
            $this.parents('.shortcode-dialog').hide();

            // If the media frame already exists, reopen it.
            if ($frame) {
                $frame.open();
                return;
            }

            // Create the media frame.
            $frame = wp.media.frames.downloadable_file = wp.media({
                title: $title,
                button: {
                    text: $btn_name
                },
                multiple: false
            });

            // When a file is selected, run a callback.
            $frame.on('select', function () {
                var $selection = $frame.state().get('selection');
                $selection.map(function ($attachment) {
                    $attachment = $attachment.toJSON();
                    if($attachment.mime != 'text/csv'){
                        alert('Selected file is not a CSV file!');
                    }else{
                        if ($attachment.id) {
                            $id = $attachment.id;
                            $.ajax({
                                type: 'post',
                                url: ajaxurl,
                                data: {
                                    action: 'super_prepare_contact_entry_import',
                                    file_id: $id
                                },
                                success: function (result) {
                                    var $result = jQuery.parseJSON(result);
                                    console.log($result);
                                    $html = '';
                                    $html += '<div class="image"><img src="' + $attachment.icon + '" /></div>';
                                    $html += $attachment.filename;
                                    $html += '<a href="#" class="delete">Delete</a>';

                                    $dropdown_type = '<select name="type">';
                                    $dropdown_type += '<option value="">Field (default)</option>';
                                    $dropdown_type += '<option value="">File</option>';
                                    $dropdown_type += '<option value="">Form ID</option>';
                                    $dropdown_type += '</select>';

                                    $html += '<ul class="import-column-connections">';
                                    $.each($result, function( index, value ) {
                                        $html += '<li>';
                                        $dropdown = '<select name="column">';
                                        $dropdown += '<option value="">Form data (default)</option>';
                                        var $lower_case_value = value.toLowerCase();
                                        if( ( $lower_case_value=='user_id' ) || ( $lower_case_value=='user' ) || ( $lower_case_value=='id' ) ) {
                                            $dropdown += '<option value="user_id" selected="selected">User ID (author)</option>';
                                        }else{
                                            $dropdown += '<option value="user_id">User ID (author)</option>';
                                        }
                                        if( ( $lower_case_value=='post_title' ) || ( $lower_case_value=='title' ) || ( $lower_case_value=='name' ) ) {
                                            $dropdown += '<option value="post_title" selected="selected">Contact Entry Title (Post Title)</option>';
                                        }else{
                                            $dropdown += '<option value="post_title">Contact Entry Title (Post Title)</option>';
                                        }
                                        if( ( $lower_case_value=='date' ) || ( $lower_case_value=='publish_date' ) ) {
                                            $dropdown += '<option value="date" selected="selected">Date (publish date)</option>';
                                        }else{
                                            $dropdown += '<option value="date">Date (publish date)</option>';
                                        }
                                        if( ( $lower_case_value=='ip' ) || ( $lower_case_value=='ip_address' ) ) {
                                            $dropdown += '<option value="ip_address" selected="selected">IP address</option>';
                                        }else{
                                            $dropdown += '<option value="ip_address">IP address</option>';
                                        }
                                        $dropdown += '</select>';
                                        $html += '<label><span>Save as: </span>'+$dropdown+'</label>';
                                        $html += '<label><span>Field Type: </span>'+$dropdown_type+'</label>';
                                        $html += '<label><span>Field Label: </span><input type="text" name="label" value="'+value+'" /></label>';
                                        $html += '<label><span>Field Name: </span><input type="text" name="name" value="'+value+'" /></label>';
                                        $html += '</li>';
                                    });
                                    $html += '</ul>';

                                    $html += '<span class="button super-button super-prepare-import"><i class="fa fa-cogs"></i> Click here to start the Import</span>';
                                    $preview.html($html);
                                }
                            });
                            
                        }
                    }
                });
                $field.val($id);
            });

            $frame.on('close', function () {
                $('.ui-widget-overlay').show();
                $this.parents('.shortcode-dialog').show();
            });
            
            // Finally, open the modal.
            $frame.open();
            
        });
    });

    $('.browse-images, .browse-files').each(function () {
        var $this = $(this);
        var $title = 'Select an Image';
        var $btn_name = 'Add Image';
        var $button = $this.children('.button');
        var $preview = $this.children('.image-preview');
        var $field = $this.children('input');
        var $frame;
        var $id = $field.val();
        if($this.hasClass('browse-files')){
            $title = 'Select an File';
            $btn_name = 'Add File';
            $preview = $this.children('.file-preview');
        }
        $preview.on('click', 'a.delete', function () {
            $field.val('');
            $preview.html('');
        });
        $button.on('click', function () {
            $('.ui-widget-overlay').hide();
            $this.parents('.shortcode-dialog').hide();

            // If the media frame already exists, reopen it.
            if ($frame) {
                $frame.open();
                return;
            }

            // Create the media frame.
            $frame = wp.media.frames.downloadable_file = wp.media({
                title: $title,
                button: {
                    text: $btn_name
                },
                multiple: false
            });

            // When an image is selected, run a callback.
            if($this.hasClass('browse-images')){
                $frame.on('select', function () {
                    var $selection = $frame.state().get('selection');
                    $selection.map(function ($attachment) {
                        $attachment = $attachment.toJSON();
                        if ($attachment.id) {
                            //$id = $id ? $id + "," + $attachment.id : $attachment.id;
                            $id = $attachment.id;
                            var $url = $attachment.url;
                            if ($attachment.sizes.thumbnail) $url = $attachment.sizes.thumbnail.url;
                            $preview.html('<div class="image"><img src="' + $url + '" /></div><a href="#" class="delete">Delete</a>');
                        }
                    });
                    $field.val($id);

                });
            }

            // When a file is selected, run a callback.
            if($this.hasClass('browse-files')){
                $frame.on('select', function () {
                    var $selection = $frame.state().get('selection');
                    $selection.map(function ($attachment) {
                        $attachment = $attachment.toJSON();
                        if ($attachment.id) {
                            //$id = $id ? $id + "," + $attachment.id : $attachment.id;
                            $id = $attachment.id;
                            $preview.html('<div class="image"><img src="' + $attachment.icon + '" /></div><a href="">' + $attachment.filename + '</a><a href="#" class="delete">Delete</a>');
                        }
                    });
                    $field.val($id);
                });
            }

            $frame.on('close', function () {
                $('.ui-widget-overlay').show();
                $this.parents('.shortcode-dialog').show();
            });
            
            // Finally, open the modal.
            $frame.open();
            
        });
    });
}

jQuery(document).ready(function ($) {
    
    var $doc = $(document);
    
    $doc.on('click', '.super-form-button > a', function () {
        SUPER.conditional_logic();
        SUPER.validate_form($(this).parents('.super-form'));
        return false;
    });
    
});